    public Cursor getTranslations(String word, int originalLanguaje)
    {
        Cursor c = getReadableDatabase().query(
                TranslationContract.TranslationEntry.TABLE_NAME,
                null,
                TranslationContract.TranslationEntry.WORD + " LIKE ? AND "+
                        TranslationContract.TranslationEntry.LANGUAGE_ID + " = ?",
                new String[]{word,String.valueOf(originalLanguaje)},
                null,
                null,
                null);
        return c;

    }

    public List<Translation> getTranslationsObject(String word, int originalLanguaje)
    {
        List<Translation> result = new ArrayList<Translation>();

        Cursor cursor = getReadableDatabase().query(
                TranslationContract.TranslationEntry.TABLE_NAME,
                null,
                TranslationContract.TranslationEntry.WORD + " LIKE ? AND "+
                        TranslationContract.TranslationEntry.LANGUAGE_ID + " = ?",
                new String[]{word,String.valueOf(originalLanguaje)},
                null,
                null,
                null);
        try {
            while (cursor.moveToNext()) {
                Translation ntranslation = new Translation();
                ntranslation.setTranslationResult(cursor.getString(cursor.getColumnIndex(TranslationContract.TranslationEntry.TRANSLATION)));
                ntranslation.setContext(cursor.getString(cursor.getColumnIndex(TranslationContract.TranslationEntry.CONTEXT)));
                ntranslation.setWordToFind(cursor.getString(cursor.getColumnIndex(TranslationContract.TranslationEntry.WORD)));
               result.add(ntranslation);

            }
        } finally {
            cursor.close();
        }
        return result;

    }


    public Cursor getPredictiveTranslations(String word , int originalLanguaje)
    {

        Cursor cursor = getReadableDatabase().query(true,
                TranslationContract.TranslationEntry.TABLE_NAME,
                new String[]{"_id",TranslationContract.TranslationEntry.WORD,
                        TranslationContract.TranslationEntry.LANGUAGE_ID,
                        TranslationContract.TranslationEntry.TRANSLATION
                },
                TranslationContract.TranslationEntry.WORD + " LIKE ? AND " +
                        TranslationContract.TranslationEntry.LANGUAGE_ID + " = ?",
                new String[]{"%"+word+"%", String.valueOf(originalLanguaje)},
                TranslationContract.TranslationEntry.WORD,
                null,
                TranslationContract.TranslationEntry.WORD+" ASC",
                null);

        return cursor;


    }

    public Boolean addTranslation(Translation translation)
    {
            try
            {
                if(!wasDownloaded(translation))
                {
                    ContentValues nuevoRegistro = new ContentValues();
                    nuevoRegistro.put(TranslationContract.TranslationEntry.LANGUAGE_ID, translation.getCountryId());
                    nuevoRegistro.put(TranslationContract.TranslationEntry.WORD,translation.getWordToFind().toLowerCase().trim());
                    nuevoRegistro.put(TranslationContract.TranslationEntry.CONTEXT,translation.getContext());
                    nuevoRegistro.put(TranslationContract.TranslationEntry.TYPE,translation.getType());
                    nuevoRegistro.put(TranslationContract.TranslationEntry.TRANSLATION,translation.getTranslationResult());
                    nuevoRegistro.put(TranslationContract.TranslationEntry.WEB_ID,translation.getWebId());

                    getReadableDatabase().insert(TranslationContract.TranslationEntry.TABLE_NAME, null, nuevoRegistro);

                    return true;

                }
                return true;

            }
            catch (Exception ex)
            {
                return false;
            }

    }

    public Boolean delTranslation(Translation translation)
    {
        return getReadableDatabase().delete(TranslationContract.TranslationEntry.TABLE_NAME, TranslationContract.TranslationEntry._ID + "=" + translation.getId(), null) > 0;
    }

    public Cursor getAllWords() {
        return getReadableDatabase()
                .query(
                        TranslationContract.TranslationEntry.TABLE_NAME,
                        null,
                        null,
                        null,
                        null,
                        null,
                        null);
    }

    public Boolean wasDownloaded(Translation translation)
    {
        try
        {
            Cursor cursor = getReadableDatabase().rawQuery("SELECT "+ TranslationContract.TranslationEntry._ID
                    +" FROM "+ TranslationContract.TranslationEntry.TABLE_NAME+" WHERE "+ TranslationContract.TranslationEntry.WEB_ID +" = ?", new String[] {translation.getWebId().toString()});

            return cursor.getCount() > 0;
        }
        catch (Exception ex)
        {
            return false;
        }

    }

